import React, { useState } from "react";
import { getReportDownload } from "../api"; // –ò–º–ø–æ—Ä—Ç API –º–µ—Ç–æ–¥–∞
import ErrorModal from "./ErrorModal"; // –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–ª—è –æ—à–∏–±–æ–∫

const Report = () => {
    const [dateFrom, setDateFrom] = useState("");
    const [dateTo, setDateTo] = useState("");
    const [report, setReport] = useState(null); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –æ—Ç—á–µ—Ç–∞
    const [error, setError] = useState(null);

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á–µ—Ç–∞
    const fetchReport = async () => {
        try {
            const data = await getReportDownload(dateFrom, dateTo);
            console.log("Fetched report data: ", data);  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–∞–Ω–Ω—ã—Ö, –ø–æ–ª—É—á–µ–Ω–Ω—ã—Ö —Å API
            setReport(data);  // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –æ—Ç—á–µ—Ç–∞
        } catch (err) {
            setError("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö: " + err.message);  // –ü–æ–∫–∞–∑–∞—Ç—å —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
        }
    };

    // –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã
    const handleSubmit = (e) => {
        e.preventDefault();
        fetchReport(); // –ó–∞–ø—Ä–æ—Å–∏—Ç—å –æ—Ç—á–µ—Ç –¥–ª—è –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –¥–∏–∞–ø–∞–∑–æ–Ω–∞ –¥–∞—Ç
    };

    // –ó–∞–∫—Ä—ã—Ç–∏–µ –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞ –æ—à–∏–±–∫–∏
    const closeErrorModal = () => {
        setError(null);
    };

    return (
        <div className="content">
            <h1>–û—Ç—á–µ—Ç</h1>

            {/* –§–æ—Ä–º–∞ –¥–ª—è –≤—ã–±–æ—Ä–∞ –¥–∏–∞–ø–∞–∑–æ–Ω–∞ –¥–∞—Ç */}
            <form onSubmit={handleSubmit}>
                <div className="date-picker">
                    <label>
                        –î–∞—Ç–∞ –æ—Ç:
                        <input
                            type="date"
                            value={dateFrom}
                            onChange={(e) => setDateFrom(e.target.value)}
                        />
                    </label>
                </div>
                <div className="date-picker">
                    <label>
                        –î–∞—Ç–∞ –ø–æ:
                        <input
                            type="date"
                            value={dateTo}
                            onChange={(e) => setDateTo(e.target.value)}
                        />
                    </label>
                </div>
                <button className="fetch-btn" type="submit">–ü–æ–ª—É—á–∏—Ç—å –æ—Ç—á–µ—Ç</button>
            </form>

            {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Å –æ—à–∏–±–∫–æ–π */}
            {error && <ErrorModal message={error} onClose={closeErrorModal}/>}

            {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—Ç—á–µ—Ç–∞ */}
            <div className="report-container">
                {report ? (
                    <div className="report-card">
                        <h2>–û—Ç—á–µ—Ç —Å {new Date(dateFrom).toLocaleDateString()} –ø–æ {new Date(dateTo).toLocaleDateString()}</h2>

                        {/* –ö–∞—Ä—Ç–æ—á–∫–∏ —Å –ø–æ–∫–∞–∑–∞—Ç–µ–ª—è–º–∏ */}
                        <div className="report-stats">
                            <div className="report-item">
                                <div className="icon">üí∞</div>
                                <div className="info">
                                    <h3>–ü–æ—Ç–µ—Ä–∏ –≤ –¥–æ—Ö–æ–¥–∞—Ö</h3>
                                    <p>{report.lost_revenue}‚ÇΩ</p>
                                </div>
                            </div>

                            <div className="report-item">
                                <div className="icon">üìä</div>
                                <div className="info">
                                    <h3>–ß–∏—Å—Ç–∞—è –ø—Ä–∏–±—ã–ª—å</h3>
                                    <p>{report.net_profit}‚ÇΩ</p>
                                </div>
                            </div>

                            <div className="report-item">
                                <div className="icon">üí≥</div>
                                <div className="info">
                                    <h3>–°—Ä–µ–¥–Ω–∏–π —á–µ–∫ –ø–æ—Å—Ç–∞–≤–∫–∏</h3>
                                    <p>{report.average_supply_check}‚ÇΩ</p>
                                </div>
                            </div>

                            <div className="report-item">
                                <div className="icon">üì¶</div>
                                <div className="info">
                                    <h3>–°—Ä–µ–¥–Ω–∏–π —á–µ–∫ –∑–∞–∫–∞–∑–∞</h3>
                                    <p>{report.average_order_check}‚ÇΩ</p>
                                </div>
                            </div>

                            <div className="report-item">
                                <div className="icon">üõí</div>
                                <div className="info">
                                    <h3>–°—É–º–º–∞ –∑–∞—Ç—Ä–∞—Ç –Ω–∞ –ø–æ—Å—Ç–∞–≤–∫—É</h3>
                                    <p>{report.amount_supply_costs}‚ÇΩ</p>
                                </div>
                            </div>
                        </div>

                    </div>
                ) : (
                    <p>–ó–∞–≥—Ä—É–∑–∏—Ç–µ –æ—Ç—á–µ—Ç –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö.</p>
                )}
            </div>
        </div>
    );
};

export default Report;
