import React, { useState } from "react";
import { getDishesWithIngredients, getDishesByIngredients, getMostPopularDishes } from "../api";

const Dishes = () => {
    const [dishes, setDishes] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const [ingredient, setIngredient] = useState("");
    const [activeTab, setActiveTab] = useState("all"); // Вкладка по умолчанию - все блюда
    const [expiringProducts, setExpiringProducts] = useState([]);

    // Функции для получения данных
    const fetchAllDishes = async () => {
        setLoading(true);
        setError(null);
        try {
            const data = await getDishesWithIngredients();
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const fetchDishesByIngredient = async () => {
        setLoading(true);
        setError(null);
        try {
            const data = await getDishesByIngredients(ingredient);
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const fetchPopularDishes = async () => {
        setLoading(true);
        setError(null);
        try {
            const data = await getMostPopularDishes();
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const fetchExpiringProducts = async () => {
        try {
            const data = await getExpiringProducts();
            setExpiringProducts(data);
            setDishes([]);
        } catch (err) {
            setError(err.message);
        }
    };

    // Контент для текущей активной вкладки
    const renderContent = () => {
        switch (activeTab) {
            case "all":
                return (
                    <>
                        <button onClick={fetchAllDishes} className="fetch-button">
                            Показать все блюда
                        </button>
                        {loading && <p>Загрузка...</p>}
                        {error && <div className="error">Ошибка: {error}</div>}
                        <div className="dishes-list">
                            {dishes.map((dish) => (
                                <div key={dish.dish_id} className="dish">
                                    <h2>{dish.name}</h2>
                                    <p>Цена: {dish.price}₽</p>
                                    <p>Ингредиенты: {dish.ingredients?.join(", ")}</p>
                                </div>
                            ))}
                        </div>
                    </>
                );
            case "search":
                return (
                    <>
                        <div className="ingredient-search">
                            <input
                                type="text"
                                placeholder="Введите ингредиент"
                                value={ingredient}
                                onChange={(e) => setIngredient(e.target.value)}
                            />
                            <button onClick={fetchDishesByIngredient} className="fetch-button">
                                Поиск по ингредиенту
                            </button>
                        </div>
                        {loading && <p>Загрузка...</p>}
                        {error && <div className="error">Ошибка: {error}</div>}
                        <div className="dishes-list">
                            {dishes.map((dish) => (
                                <div key={dish.dish_id} className="dish">
                                    <h2>{dish.name}</h2>
                                    <p>Цена: {dish.price}₽</p>
                                    <p>Ингредиенты: {dish.ingredients?.join(", ")}</p>
                                </div>
                            ))}
                        </div>
                    </>
                );
            case "popular":
                return (
                    <>
                        <button onClick={fetchPopularDishes} className="fetch-button">
                            Показать популярные блюда
                        </button>
                        {loading && <p>Загрузка...</p>}
                        {error && <div className="error">Ошибка: {error}</div>}
                        <div className="dishes-list">
                            {dishes.map((dish) => (
                                <div key={dish.dish_id} className="dish">
                                    <h2>{dish.name}</h2>
                                    <p>Цена: {dish.price}₽</p>
                                    <p>Ингредиенты: {dish.ingredients?.join(", ")}</p>
                                    {dish.ordered_count && <p>Заказано: {dish.ordered_count} раз</p>}
                                </div>
                            ))}
                        </div>
                    </>
                );
            default:
                return null;
        }
    };

    return (
        <div className="content">
            <h1>Меню</h1>
            <div className="navbar">
                <button onClick={() => setActiveTab("all")} className={activeTab === "all" ? "active-tab" : ""}>
                    Все блюда
                </button>
                <button onClick={() => setActiveTab("search")} className={activeTab === "search" ? "active-tab" : ""}>
                    Поиск по ингредиенту
                </button>
                <button onClick={() => setActiveTab("popular")} className={activeTab === "popular" ? "active-tab" : ""}>
                    Популярные блюда
                </button>
            </div>
            {renderContent()}
        </div>
    );
};

export default Dishes;
