// Report.js
import React, { useState } from 'react';
import axios from 'axios';

const Report = () => {
    const [dateFrom, setDateFrom] = useState('');
    const [dateTo, setDateTo] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleDownload = async () => {
        if (!dateFrom || !dateTo) {
            alert("Пожалуйста, укажите даты.");
            return;
        }

        setIsLoading(true);
        setError('');

        try {
            const response = await axios.get(`/api/report/download`, {
                params: {
                    date_from: dateFrom,
                    date_to: dateTo,
                },
                responseType: 'blob', // указываем, что ожидаем бинарный файл
            });

            const blob = response.data;
            const link = document.createElement('a');
            link.href = URL.createObjectURL(blob);
            link.download = `report_${dateFrom}_${dateTo}.pdf`; // Название файла
            link.click();
        } catch (err) {
            setError('Ошибка при скачивании отчета');
            console.error(err);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h1>Отчет</h1>
            <div>
                <label>
                    Дата начала:
                    <input
                        type="date"
                        value={dateFrom}
                        onChange={(e) => setDateFrom(e.target.value)}
                    />
                </label>
            </div>
            <div>
                <label>
                    Дата окончания:
                    <input
                        type="date"
                        value={dateTo}
                        onChange={(e) => setDateTo(e.target.value)}
                    />
                </label>
            </div>
            <div>
                <button onClick={handleDownload} disabled={isLoading}>
                    {isLoading ? 'Загружаю...' : 'Скачать отчет'}
                </button>
            </div>
            {error && <div style={{ color: 'red' }}>{error}</div>}
        </div>
    );
};

export default Report;
