import React, { useState } from "react";
import { getDishesWithIngredients, getDishesByIngredients, getMostPopularDishes } from "../api";

const Dishes = () => {
    const [dishes, setDishes] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const [ingredient, setIngredient] = useState("");

    // Обработчик для получения всех блюд с ингредиентами
    const fetchAllDishes = async () => {
        setLoading(true);
        setError(null);
        try {
            const data = await getDishesWithIngredients();
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // Обработчик для поиска блюд по ингредиентам
    const fetchDishesByIngredient = async () => {
        setLoading(true);
        setError(null);
        try {
            const data = await getDishesByIngredients(ingredient);
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // Обработчик для получения самых популярных блюд
    const fetchPopularDishes = async () => {
        setLoading(true);
        setError(null);
        try {
            const data = await getMostPopularDishes();
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="content">
            <h1>Меню</h1>

            {/* Кнопки для запуска разных функций */}
            <button onClick={fetchAllDishes} className="fetch-button">
                Показать все блюда
            </button>

            <div className="ingredient-search">
                <input
                    type="text"
                    placeholder="Введите ингредиент"
                    value={ingredient}
                    onChange={(e) => setIngredient(e.target.value)}
                />
                <button onClick={fetchDishesByIngredient} className="fetch-button">
                    Поиск по ингредиенту
                </button>
            </div>

            <button onClick={fetchPopularDishes} className="fetch-button">
                Показать популярные блюда
            </button>

            {loading && <p>Загрузка...</p>}

            {error && <div className="error">Ошибка: {error}</div>}

            {/* Список блюд */}
            {dishes.length > 0 && (
                <div className="dishes-list">
                    {dishes.map((dish) => (
                        <div key={dish.dish_id} className="dish">
                            <h2>{dish.name}</h2>
                            <p>Цена: {dish.price}₽</p>
                            <p>Ингредиенты: {dish.ingredients?.join(", ")}</p>
                            {dish.ordered_count && <p>Заказано: {dish.ordered_count} раз</p>}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Dishes;
