// src/components/Dishes.js
import React, { useState, useEffect } from "react";
import { getDishesWithIngredients } from "../api";

const Dishes = () => {
    const [dishes, setDishes] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDishes = async () => {
            try {
                const data = await getDishesWithIngredients();
                setDishes(data);
            } catch (err) {
                setError(err.message);
            }
        };
        fetchDishes();
    }, []);

    if (error) {
        return <div className="error">Ошибка: {error}</div>;
    }

    return (
        <div className="content">
            <h1>Блюда и ингредиенты</h1>
            {dishes.map((dish) => (
                <div key={dish.dish_id} className="dish">
                    <h2>{dish.name}</h2>
                    <p>Цена: {dish.price}₽</p>
                    <p>Ингредиенты: {dish.ingredients.join(", ")}</p>
                </div>
            ))}
        </div>
    );
};

export default Dishes;
