// Sidebar.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
    const location = useLocation(); // Получаем текущий путь

    return (
        <div className="sidebar">
            <h1>Restaurant Management</h1>
            <ul>
                <li>
                    <Link to="/dishes" className={location.pathname === "/dishes" ? "active" : ""}>Dishes</Link>
                </li>
                <li>
                    <Link to="/employees" className={location.pathname === "/employees" ? "active" : ""}>Employees</Link>
                </li>
                <li>
                    <Link to="/products" className={location.pathname === "/products" ? "active" : ""}>Products</Link>
                </li>
                <li>
                    <Link to="/customers" className={location.pathname === "/customers" ? "active" : ""}>Customers</Link>
                </li>
            </ul>
        </div>
    );
};

export default Sidebar;
