import React, { useState } from "react";
import { getDishesWithIngredients } from "../api";

const Dishes = () => {
    const [dishes, setDishes] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    // Функция для загрузки блюд, вызывается по нажатию кнопки
    const fetchDishes = async () => {
        setLoading(true); // Устанавливаем состояние загрузки
        setError(null);   // Очищаем ошибки
        try {
            const data = await getDishesWithIngredients();
            setDishes(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false); // Завершаем состояние загрузки
        }
    };

    return (
        <div className="content">
            <h1>Меню</h1>
            <button onClick={fetchDishes} className="fetch-button">
                Показать блюда
            </button>

            {loading && <p>Загрузка...</p>}

            {error && <div className="error">Ошибка: {error}</div>}

            {dishes.length > 0 && (
                <div className="dishes-list">
                    {dishes.map((dish) => (
                        <div key={dish.dish_id} className="dish">
                            <h2>{dish.name}</h2>
                            <p>Цена: {dish.price}₽</p>
                            <p>Ингредиенты: {dish.ingredients.join(", ")}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Dishes;
